// добавление значений в

import "./App.css";
import jwt_decode from "jwt-decode";
import { useEffect, useState } from "react";
import { useLocalState } from "./util/useLocalStorage";
import Dashboard from "./Dashboard";
import { Routes, Route } from "react-router-dom";
import HomePage from "./HomePage";
import Login from "./Login";
import PrivateRoute from "./PrivateRoute";
import AssignmentView from "./AssignmentView";
// import "bootstrap/dist/css/bootstrap.min.css";
import CodeReviwerDashboard from "./CodeReviewerDashboard";
import CodeReviewAssignmentView from "./CodeReviewAssignmentView";
import { UserProfilePage } from "./Pages/UserProfilePage";

function App() {
  const [jwt, setJwt] = useLocalState("", "jwt");
  const [roles, setRoles] = useState(getRoleFromJWT());

  function getRoleFromJWT() {
    if (jwt) {
      const decodedJwt = jwt_decode(jwt);
      return decodedJwt.authorities;
    }
    return [];
  }

  useEffect(() => {
    console.log(jwt);
    console.log(roles);
  }, [jwt]);

  return (
    <>
      <Routes>
        <Route
          path="dashboard"
          element={
            roles.find((role) => role === "ROLE_CODE_REVIEWER") ? (
              <PrivateRoute>
                <CodeReviwerDashboard />
              </PrivateRoute>
            ) : (
              <PrivateRoute>
                <Dashboard />
              </PrivateRoute>
            )
          }
        />
        <Route
          path="/assignments/:id"
          element={
            roles.find((role) => role === "ROLE_CODE_REVIEWER") ? (
              <PrivateRoute>
                <CodeReviewAssignmentView />
              </PrivateRoute>
            ) : (
              <PrivateRoute>
                <AssignmentView />
              </PrivateRoute>
            )
          }
        />
        <Route path="/" element={<HomePage />} />

        <Route
          path="/Profile"
          element={
            <PrivateRoute>
              <UserProfilePage />
            </PrivateRoute>
          }
        />

        <Route path="login" element={<Login />} />
      </Routes>
    </>
  );
}

export default App;
