import React, { useEffect } from "react";
import { useLocalState } from "../util/useLocalStorage";
import jwt_decode from "jwt-decode"

import { useState } from "react";
import { Link } from "react-router-dom";
import ajax from "../Services/fetchService";
import { Button, Card, Row, Col, Badge, Container } from "react-bootstrap";

const CodeReviwerDashboard = () => {
  const [jwt, setJwt] = useLocalState("", "jwt");
  const [assignments, setAssignments] = useState(null);

  useEffect(() => {
    ajax("api/assignments", "GET", jwt).then((assignmentsData) => {
      setAssignments(assignmentsData);
    });
  }, []);

  function createAssigment() {
    ajax("api/assignments", "POST", jwt).then((assignment) => {
      window.location.href = `/assignments/${assignment.id}`;
    });
  }

  function claimAssignment(assignment){
	
	const decodeJwt = jwt_decode(jwt);
	const user = {
		username:decodeJwt.sub,
		authorities: decodeJwt.authorities,
	};


	assignment.codeReviewer = user; 

	assignment.status = "In Review";

	ajax(`/api/assignments/${assignment.id}`,"PUT",jwt, assignment).then(( updateAssignment) => {
		
		const assignmentsCopy = [...assignments];
		const i = assignmentsCopy.findIndex((a) => a.id ===  assignment.id);
		assignmentsCopy[i] = updateAssignment;
		setAssignments(assignmentsCopy);
	})

  }

  return (
    <Container>

      <Row>
        <Col>
          <div
            className="d-flex justify-content-end"
            style={{cursor: "pointer"}}
            onClick={() => {
              setJwt(null);
              window.location.href="/login";
            }}
          >
            Logout
          </div>
        </Col>
      </Row>

		<h1 className="mb-5">Code Reviewer</h1>

		<div className="assignment-wrapper  in-review">
		<div 
			className="assignment-wrapper-title h3 px-2 my-2"
			
			>In Review</div>

{assignments.map((assignment) => (
            
            <Card key={assignment.id} style={{ width: "18rem" }}>
              <Card.Body className="d-flex flex-column justify-content-around">
                <Card.Title>Assigment #{assignment.number}</Card.Title>
                <div className="d-flex align-items-start">
                  <Badge
                    pill
                    bg="info"
                    style={{ fontSize: "1em" }}
                  >
                    {assignment.status}
                  </Badge>
                </div>
                <Card.Text className="mt-1">
                  <p>GitHub URL: {assignment.githubUrl}</p>
                  <p>Branch: {assignment.branch}</p>
                </Card.Text>

                <Button
                  variant="secondary"
                  onClick={() => {
							claimAssignment(assignment);
						}}
                >
                  Claim
                </Button>
              </Card.Body>
            </Card>

          ))}
		</div>
		<br></br>

		<div className="assignment-wrapper  in-review">
			<div 
			className="assignment-wrapper-title h3 px-2"
			
			>Awaiting Review</div>

		{/* <div className="assignment-wrapper submitted"></div>

		<div className="assignment-wrapper needs-update"></div> */}



      {assignments ? (
        <div
          className="d-grid gap-5"
          style={{ gridTemplateColumns: "repeat(auto-fit, 18rem)" }}
        >
          {assignments.map((assignment) => (
            
            <Card key={assignment.id} style={{ width: "18rem" }}>
              <Card.Body className="d-flex flex-column justify-content-around">
                <Card.Title>Assigment #{assignment.number}</Card.Title>
                <div className="d-flex align-items-start">
                  <Badge
                    pill
                    bg="info"
                    style={{ fontSize: "1em" }}
                  >
                    {assignment.status}
                  </Badge>
                </div>
                <Card.Text className="mt-1">
                  <p>GitHub URL: {assignment.githubUrl}</p>
                  <p>Branch: {assignment.branch}</p>
                </Card.Text>

                <Button
                  variant="secondary"
                  onClick={() => {
							claimAssignment(assignment);
						}}
                >
                  Claim
                </Button>
              </Card.Body>
            </Card>

          ))}
        </div>
      ) : (
        <></>
      )}
		</div>
      {/* <button onClick={() => createAssigment()}>Submit new Assigment</button> */}
    </Container>
  );
  
};

export default CodeReviwerDashboard;

//rsc
